import javax.mail.internet.*
import groovy.json.JsonSlurper

def projectName = build.buildVariableResolver.resolve("PROJECT_NAME_PARAM")
def version = build.buildVariableResolver.resolve("VERSION_PARAM")
def param = "jql=project="+projectName+"%20AND%20fixVersion="+version
def connection = new URL( "https://sivaltec.atlassian.net/rest/api/2/search?"+param).openConnection() as HttpURLConnection

// set some headers
connection.setRequestProperty( 'User-Agent', 'groovy-2.4.4' )
connection.setRequestProperty( 'Accept', 'application/json' )
connection.setRequestProperty( 'Authorization', 'Basic ' + 'ka.bassirou22@gmail.com:kobebryan123'.bytes.encodeBase64().toString())

def messageContent = ""
if ( connection.responseCode == 200 ) {

    // get the JSON response
    def json = connection.inputStream.withCloseable { inStream ->
        new JsonSlurper().parse( inStream as InputStream )
    }

    // extract some data from the JSON, printing a report
    def item = json.issues
   // println item.title
   // println "Temperature: ${item.condition?.temp}, Condition: ${item.condition?.text}"

    // show some forecasts
   // println "Forecasts:"
messageContent+="<p> Ci-dessous la liste des Ã©volutions ou anomalies inclues dans cette livraison </p>"
messageContent+="<ul>"
    item.forecast.each { f ->
       messageContent += " <li> <a href= 'https://sivaltec.atlassian.net/browse/${item.key[0]}'>  ${item.fields.summary[0]} </a> </li>"
      //  println " * ${f.date} - Low: ${f.low}, High: ${f.high}, Condition: ${f.text}"
 messageContent+="</ul>"
    }
} else {
    println connection.responseCode + ": " + connection.inputStream.text
}


logger.print("This ends up in the log as well");
String encodingOptions = "text/html; charset=UTF-8;";
msg.setContent(messageContent, encodingOptions);
msg.setSubject("Livraison de la version "+version+" de l'application "+projectName+".", encodingOptions);
